import React, {Component} from 'react';
import Link from 'next/link'
import Route from 'next/router'
import {CSSTransitionGroup} from 'react-transition-group'


class HomePage extends Component {
    static getInitialProps() {
        return { title: 'Home Title' }
    }
    constructor () {
        this.state = {
            isShow: true
        }
    }

    render() {
        return (
            <div>
                <div>{this.props.title}</div>
                <div className="toggle" onClick = {()=> this.setState(prevState => ({isShow: !prevState.isShow}))}>
                    Toggle
                </div>

                <a>
                    <Link href="/auth">Auth</Link>
                </a>

                <button onClick={() => Route.push("/auth")}>Auth Route</button>
                
                <CSSTransitionGroup in={this.state.isShow} timeout={1000} unmountOnExit mountOnEnter>
                    <div className="my-div">
                        
                    </div>
                </CSSTransitionGroup>

                <style jsx> {`
                .my-div {
                    color: red;
                    width: 100px;
                    height: 100px;
                    background-color: red;
                }
            `}
                </style>
            </div>
        );
    }
}




export default HomePage;
